import { Query, Mutation } from '@farfetched/core';

/**
 * @deprecated Use `useUnit` from 'effector-react` instead
 */
declare function useQuery<Params, Data, Error, InitialData>(query: Query<Params, Data, Error, InitialData>): {
    data: Data | InitialData;
    error: Error | null;
    stale: boolean;
    pending: boolean;
    start: (params: Params) => void;
};

/**
 * @deprecated Use `useUnit` from 'effector-react` instead
 */
declare function useMutation(mutation: Mutation<void, any, any>): {
    start: () => void;
    pending: boolean;
};
/**
 * @deprecated Use `useUnit` from 'effector-react` instead
 */
declare function useMutation<P>(mutation: Mutation<P, any, any>): {
    start: (params: P) => void;
    pending: boolean;
};

export { useMutation, useQuery };
